package mainmethod;

public class Addition {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 24;
		int b = 2;
		int sum = a +b;
		System.out.println(sum);
		int sub = a-b;
		System.out.println(sub);
		int mul = a*b;
		System.out.println(mul);
		int div = a/b;
		System.out.println(div);
		int mod = a%b;
		System.out.println(mod);
		
	}

}
